import React from 'react';

import './App.css';

const [showParagraph, setShowParagraph] = useState(false);

function App() {
  return (
    <div className="app" onClick={setShowParagraph(true)}>
      <h1>Hi there!</h1>
      {showParagraph && <p>This is new!</p> }
    </div>
  );
}

export default App;
